sys_variable_value.value:
(function execute(inputs, outputs) {

  new AzureComputeUtils().checkIfEmpty(inputs);
  var util = new JSONBuilder();
  var payload ={};
  var properties ={};
  var vmSize = util.createObjectElement('vmSize',inputs.vmSize);
  properties.hardwareProfile = vmSize;
  payload.properties = properties;

  outputs.payload = JSON.stringify(payload);

})(inputs, outputs);

