sys_variable_value.value:
(function execute(inputs, outputs) {

  var successCodes = ['200', '202', '201'];
  var arrayUtil = new global.ArrayUtil();
  if (arrayUtil.contains(successCodes, inputs.status)) {
    var responseBody = JSON.parse(inputs['responseBody']);
    var virtualMachines = responseBody['value'];
    var listVirtualMachines =[];
    var data=[];
    virtualMachines.forEach(function(property){
      listVirtualMachines.push({
        label:property.name,
        name :property.id
      });
    }); 
    outputs.listvirtualmachines = {
      data:listVirtualMachines
    };
    outputs.status = 'Success';
  } else {
    new AzureComputeUtils().postErrorHandling(inputs, outputs);
  }
})(inputs, outputs);

