sys_variable_value.value:
(function execute(inputs, outputs) {
  var mandatoryInputs = {};
  mandatoryInputs.subscriptionID = inputs.subscriptionID;
  mandatoryInputs.resourceGroupName = inputs.resourceGroupName;
  mandatoryInputs.virtualMachine = inputs.virtualMachine;
  mandatoryInputs.location = inputs.location;
  new AzureComputeUtils().checkIfEmpty(mandatoryInputs);
  var util = new JSONBuilder();
  var vmSize = util.createObjectElement('vmSize',inputs.hardwareProfile);
  var storageAccountType = util.createObjectElement('storageAccountType',inputs.managedDisk);
  var payload ={};
  var properties={};
  var storageProfile ={};
  var networkInterfaces =[];
  var networkProfile = {};
  payload.location = inputs.location;
  storageProfile.imageReference = {'id':inputs.sourceImageId};
  inputs.osDisk = util.addObjectElement(inputs.osDisk,'managedDisk',storageAccountType);
  storageProfile.osDisk = inputs.osDisk;
  properties.storageProfile = storageProfile;
  properties.hardwareProfile = vmSize;
  properties.osProfile = inputs.osProfile;
  util.addArrayElement(networkInterfaces,{'id':inputs.networkInterface});
  networkProfile = util.addObjectElement(networkProfile,'networkInterfaces',networkInterfaces);
  properties.networkProfile = networkProfile;
  payload.properties = properties;
  outputs.payload = JSON.stringify(payload);
})(inputs, outputs);

