sys_variable_value.value:
(function execute(inputs, outputs) {

  var successCodes = ['200', '202', '201'];
  var arrayUtil = new global.ArrayUtil();
  if (arrayUtil.contains(successCodes, inputs.status)) {
    outputs.status = 'Success';
  } else {
    new AzureComputeUtils().postErrorHandling(inputs, outputs);
  }
})(inputs, outputs);

